export function dropdownFilter(){setupDropdownFilter("ingredients-filter"),setupDropdownFilter("appareils-filter"),setupDropdownFilter("ustensiles-filter")}function changeChevron(e,t){e.classList.toggle("d-block"),t.classList.toggle("d-none")}function setupDropdownFilter(e){const t=document.getElementById(e),n=t.querySelector(".chevron-up"),o=t.querySelector(".chevron-down"),r=t.closest(".filters__container__ingredients, .filters__container__appareils, .filters__container__ustensils").querySelector(".dropdown-search");t.addEventListener("click",(()=>{changeChevron(n,o),r&&r.classList.toggle("show")})),r?.addEventListener("mouseleave",(()=>{changeChevron(t.querySelector(".chevron-up"),t.querySelector(".chevron-down")),r.classList.toggle("show")}))}